import React, { useState } from "react";
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';



const CreateNote = (props) =>{

    const [note,setNote] = useState ({
        title:"",
        content:"",
    });

    
    const inputEvent = (event) =>{
        const {name,value} = event.target;
        
        setNote((prevData) => {
            return {
                ...prevData,
                [name]: value,
            };
        });
    };

    const addEvent = () => {
        props.passNote();
    };



    return(
        <>
           <div className="main-note">
           <input type="text" placeholder="title" value={note.title} onChange={inputEvent} name="title" />
           <textarea rows="" column="" placeholder="write a note..." name="content" value={note.content} onChange={inputEvent}></textarea>
            <Button onClick={addEvent}><AddIcon/></Button>
           
           </div>
        </>
    );
}

export default CreateNote;
